mapper.select:
select count(place_id) from ( select * from place where place_addr like '%' || #{city, jdbcType=VARCHAR} || '%' and place_addr like '%' || #{district, jdbcType=VARCHAR} || '%' ) where place_rate like '%' || #{rate, jdbcType=VARCHAR} || '%'
and place_act in
#{actChk, jdbcType=VARCHAR}
and place_type in
#{typeChk, jdbcType=VARCHAR}

select * from (select rownum rnum, place.* from (select * from place where place_addr like '%' || #{city, jdbcType=VARCHAR} || '%' and place_addr like '%' || #{district, jdbcType=VARCHAR} || '%' order by place_id asc) place where place_rate like '%' || #{rate, jdbcType=VARCHAR} || '%') where rnum >= ((#{page, jdbcType=INTEGER}-1) * 5+1) and rnum <= (#{page, jdbcType=INTEGER} * 5)
and place_act in
#{actChk, jdbcType=VARCHAR}
and place_type in
#{typeChk, jdbcType=VARCHAR}
